import {
    StyleSheet,
    Text,
    View,
    Image,
    FlatList,
    TextInput,
    TouchableOpacity,
} from "react-native";
import React, { useState } from "react";
import Users from "../api/usersapi";
import Search from "./Search";
import { Ionicons } from '@expo/vector-icons';
import { Header } from "react-native/Libraries/NewAppScreen";

const UserData2 = ({ navigation, route }) => {

    const [val, setVal] = useState("");
    const [searchStu, setSearchStu] = useState();

    const userCard = ({ item }) => {

        const stulink = require('../api/usersapi');


        return (
            <View style={styles.mainContainer}>

                <View style={{
                    paddingVertical: 5,
                    width: 410,
                    right: 7,
                    position: "relative",
                    flexDirection: "row",
                    justifyContent: "space-around",
                    elevation: 5,
                    borderRadius: 15,
                    backgroundColor: "white"
                }}>

                    <TextInput style={{ width: 365, backgroundColor: "#e1e1e2", borderRadius: 10, fontSize: 18, letterSpacing: 2 }}
                        placeholder="  ðŸ”  Search Student"
                        value={val}
                        key={id}
                        //onChangeText={(text) => setValue(text)}
                        onChange={(event) => {
                            setVal(event.target.value);

                        }}
                        I />

                    <TouchableOpacity onPress={() => {
          /* ask */     Users.filter((item) => {
                        if (item.id === searchStu.value) {
                            return setSearchStu(item)
                        }
                    }).map((item, id) => {
                        return (
                            <View style={styles.userContainer}>
                                <View>
                                    <Image
                                        style={styles.cardImage}
                                        source={item.image}
                                    />
                                </View>
                                <Text style={styles.mainHeader}>ID: {item.id}</Text>
                                <Text style={styles.mainHeader}>NAME: {item.name}</Text>
                                <Text style={styles.biodata}>BIODATA</Text>
                                <Text style={styles.description}>{item.description}</Text>
                                <Text style={styles.enroll}>
                                    COURSE ENROLLED: {item.courseenroll}
                                </Text>
                            </View>
                        );
                    })
                    }}>
                        <Ionicons name="md-send" size={32} />
                    </TouchableOpacity>

                </View>

                <View style={styles.userContainer}>
                    <View>
                        <Image
                            style={styles.cardImage}
                            source={item.image}
                        />
                    </View>
                    <Text style={styles.mainHeader}>NAME: {item.name}</Text>
                    <Text style={styles.biodata}>BIODATA</Text>
                    <Text style={styles.description}>{item.description}</Text>
                    <Text style={styles.enroll}>
                        COURSE ENROLLED: {item.courseenroll}
                    </Text>
                </View>
            </View>
        );
    };
    return (
        <FlatList
            keyExtractor={(item) => item.id}
            data={Users}
            renderItem={userCard}
        />
    );
};



