                //onChangeText={(text) => setValue(text)}
                onChangeText={(text) => {
                    setSearchStud(text);
                }}
                I />

            <TouchableOpacity onPress={() => {
  /* ask */     Users.filter((item) => {
                if (searchStud === "") {
                    return console.log(item)
